import cv2import matplotlib.pyplot as pltimport numpy as np# 目标图片img = cv2.imread("../img/target.jpg")w_src, h_src = img.shape[:2]# img = src.copy()# 特征图片roi = cv2.imread("../img/45.jpg")roi_w, roi_h = roi.shape[:2]# roi = src_roi.copy()# 模板匹配res = cv2.matchTemplate(img, roi, cv2.TM_CCOEFF_NORMED)# 返回模板中最匹配的位置，确定左上角的坐标min_Val, max_Val, min_Loc, max_Loc = cv2.minMaxLoc(res)# 使用相关系数匹配时，最大值为最佳匹配位置top_left = max_Loctop_left_1 = (top_left[0] + roi_h, top_left[1] + roi_w)cropped = img[top_left[1]:top_left_1[1], top_left[0]:top_left_1[0]]cv2.imwrite("result02.jpg", cropped)